mapper.select:
SELECT USER_ID, USER_NAME, (SELECT CASE WHEN ADJUST IS NULL THEN TOTAL_ANNUAL ELSE TOTAL_ANNUAL + ADJUST END FROM ANNUAL_STATUS WHERE USER_ID = U.USER_ID) AS 'TOTAL' , (SELECT USE_ANNUAL FROM ANNUAL_STATUS WHERE USER_ID = U.USER_ID) AS 'USE', ((SELECT CASE WHEN ADJUST IS NULL THEN TOTAL_ANNUAL ELSE TOTAL_ANNUAL + ADJUST END FROM ANNUAL_STATUS WHERE USER_ID = U.USER_ID) - (SELECT USE_ANNUAL FROM ANNUAL_STATUS WHERE USER_ID = U.USER_ID)) AS 'RESULT' , (SELECT IFNULL(SUM(APPLICATION_YEAR), '') FROM APPLICATION_HISTORY WHERE USER_ID = U.USER_ID AND DATE_FORMAT(START_DATE, '%Y%m') = CONCAT(#{year}, '02')) as 'feb', (SELECT IFNULL(SUM(APPLICATION_YEAR), '') FROM APPLICATION_HISTORY WHERE USER_ID = U.USER_ID AND DATE_FORMAT(START_DATE, '%Y%m') = CONCAT(#{year}, '03')) as 'mar', (SELECT IFNULL(SUM(APPLICATION_YEAR), '') FROM APPLICATION_HISTORY WHERE USER_ID = U.USER_ID AND DATE_FORMAT(START_DATE, '%Y%m') = CONCAT(#{year}, '04')) as 'apr', (SELECT IFNULL(SUM(APPLICATION_YEAR), '') FROM APPLICATION_HISTORY WHERE USER_ID = U.USER_ID AND DATE_FORMAT(START_DATE, '%Y%m') = CONCAT(#{year}, '05')) as 'may', (SELECT IFNULL(SUM(APPLICATION_YEAR), '') FROM APPLICATION_HISTORY WHERE USER_ID = U.USER_ID AND DATE_FORMAT(START_DATE, '%Y%m') = CONCAT(#{year}, '06')) as 'jun', (SELECT IFNULL(SUM(APPLICATION_YEAR), '') FROM APPLICATION_HISTORY WHERE USER_ID = U.USER_ID AND DATE_FORMAT(START_DATE, '%Y%m') = CONCAT(#{year}, '07')) as 'jul', (SELECT IFNULL(SUM(APPLICATION_YEAR), '') FROM APPLICATION_HISTORY WHERE USER_ID = U.USER_ID AND DATE_FORMAT(START_DATE, '%Y%m') = CONCAT(#{year}, '08')) as 'aug', (SELECT IFNULL(SUM(APPLICATION_YEAR), '') FROM APPLICATION_HISTORY WHERE USER_ID = U.USER_ID AND DATE_FORMAT(START_DATE, '%Y%m') = CONCAT(#{year}, '09')) as 'sept', (SELECT IFNULL(SUM(APPLICATION_YEAR), '') FROM APPLICATION_HISTORY WHERE USER_ID = U.USER_ID AND DATE_FORMAT(START_DATE, '%Y%m') = CONCAT(#{year}, '10')) as 'oct', (SELECT IFNULL(SUM(APPLICATION_YEAR), '') FROM APPLICATION_HISTORY WHERE USER_ID = U.USER_ID AND DATE_FORMAT(START_DATE, '%Y%m') = CONCAT(#{year}, '11')) as 'nov', (SELECT IFNULL(SUM(APPLICATION_YEAR), '') FROM APPLICATION_HISTORY WHERE USER_ID = U.USER_ID AND DATE_FORMAT(START_DATE, '%Y%m') = CONCAT(#{year}, '12')) as 'dec' FROM USER_INFO U WHERE 1=1
AND USER_NAME LIKE CONCAT('%', #{user_name}, '%')
ORDER BY START_YEAR DESC LIMIT #{pageSize} OFFSET #{startIndex}

mapper.update:
UPDATE
    ANNUAL_STATUS
SET
    USE_ANNUAL = USE_ANNUAL + #{application_year}
WHERE
    USER_ID = #{user_id}
